import {useCallback, useState} from "react";
import axios from "axios";
import "./MainComponent.css";

const MainComponent = () => {
    const [userValues, setValues] = useState([]);
    const [userValue, setValue] = useState([]);
    const getAllUsers = useCallback(async() =>{
        const userValues = await axios.get('api/users/register');
        setValues(userValues);

    },[]);

    const saveUser = useCallback(async (event) =>{
        event.preventDefault();

        await axios.post('api/users/register',{
            userValue
    })
    setValue('');
    getAllUsers();
}, [userValue, getAllUsers]);

    return(
        <div>
            <button onClick={getAllUsers}>Get all Users </button><br/>
            <span className="title">User Values</span>
            <div className="userValues">
                {userValues.map((useValue)=> <div className="userValue">{userValue}</div>)}
            </div>
            <form className="form" onSubmit={saveUser}>
                <label>Enter your user details</label>
                <input value={userValue}> onChange={((event)=> {setValue(event.target.userValue)})}</input>
                <button>Submit</button>
            </form>
        </div>
    )
}

export default MainComponent;
